mapper.select:

select * from ( 
	select /*+ INDEX_DESC(tb_culture pk_culture)*/ 
	ROWNUM rn, cno, cdate, kind, title, rank, content, rdate, udate, mno 
	From tb_culture where 



	ROWNUM <= #{pageNum}*#{amount} and mno=#{mno}
) where rn > (#{pageNum}-1)*#{amount}

Select cno, cdate, kind, title, content, rank, rdate, udate, mno 
From tb_culture
Where 1=1 AND TO_CHAR(TO_DATE(cdate), 'yyyy-MM-dd') = #{sdate} and mno=#{mno}

mapper.update:
update tb_culture 
set cdate=#{cdate}, kind=#{kind}, title=#{title}, content=#{content}, rank=#{rank}, udate=sysdate
where cno=#{cno}
