mapper.select:

select * from ( 
	select /*+ INDEX_DESC(tb_culture pk_culture)*/ 
	ROWNUM rn, cno, cdate, kind, title, content, rdate, udate, mno 
	From tb_culture where ROWNUM <= #{pageNum}*#{amount}
) where rn > (#{pageNum}-1)*#{amount}

mapper.update:
update tb_culture 
set cdate=#{cdate}, kind=#{kind}, title=#{title}, content=#{content}, rank=#{rank}, udate=sysdate
where cno=#{cno}
