mapper.select:

select * from ( 
	select /*+ INDEX_DESC(tb_message, pk_message) */ 
	ROWNUM rn,  mno, sender, target, message, sdate, odate, udate, refno 
	from tb_message  where



	ROWNUM <= #{pageNum}*#{amount}
) where rn > (#{pageNum}-1)*#{amount}

mapper.update:
update tb_message 
set message=#{message}, udate=sysdate 
where mno=#{mno}
