mapper.update:
Update tb_reply 
Set reply=#{reply}, udate=sysdate 
Where rno=#{rno}

mapper.select:

Select * from (
	Select /*+ INDEX(tb_reply, idx_reply) */
	rownum rn, rno, reply, replyer, rdate, udate, bno  
	from tb_reply 
	Where bno=#{bno} and rno > 0 and rownum <= #{cri.pageNum} * #{cri.amount} 
) Where rn > (#{cri.pageNum}-1)*#{cri.amount}
